import { useEffect, useState } from "react";
import { Feriado } from "../interfaces/Feriado";

export const useFeriados = () => {
  const [feriados, setFeriados] = useState<Feriado[]>([]);
  const [error, setError] = useState<String | null>(null);

  useEffect(() => {
    fetch("/fl/feriados")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Error de comunicacion");
        }
        return response.json();
      })
      .then((data) => {
        const filteredHolidays: Feriado[] = data.filter(
          (feriado: Feriado) => new Date(feriado.fecha).getFullYear() >= 2020
        );
        setFeriados(filteredHolidays);
      })
      .catch((error) => {
        console.error("Error al obtener datos:", error);
        setError(error.message);
      });
  }, []);

  return { feriados, error };
};
